import { takeEvery, call, put } from "redux-saga/effects";

import {
  firestore,
  convertColletionToMap
} from "../../firebase/firebase.utils";

import {
  fetchCollectionsSuccess,
  fetchCollectionsFailure
} from "./shop.actions";

import ShopActionTypes from "./shops.types";

export function* fetchCollectionsAsync() {
  yield console.log("I am fired");
  try {
    const collectionRef = firestore.collection("collections");
    const snaphot = yield collectionRef.get();
    const collectionMap = yield call(convertColletionToMap, snaphot);
    yield put(fetchCollectionsSuccess(collectionMap));
  } catch (error) {
    console.log(error);
    yield put(fetchCollectionsFailure(error));
  }
  // collectionRef
  //   .get()
  //   .then(snaphot => {
  //     const collectionMap = convertColletionToMap(snaphot);
  //     // console.log("shop mount", collectionMap);
  //     dispatch(fetchCollectionsSuccess(collectionMap));
  //     this.setState({ isLoading: false });
  //   })
  //   .catch(error => console.log(error));
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    ShopActionTypes.FETCH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}
