import SHOP_DATA from "./shopdata";

import ShopActionTypes from "./shops.types";

const INITIAL_STATE = {
  collections: []
};

const shopRducer = (state = INITIAL_STATE, action) => {
  console.log(action);

  switch (action.type) {
    case "UPDATE_COLLECTIONS":
      return {
        collections: action.payload
      };
    default:
      return state;
  }
};

export default shopRducer;
