import React from "react";

import { Route } from "react-router-dom";

import { connect } from "react-redux";

import { createStructuredSelector } from "reselect";

import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions";
import { selectIsCollectionLoading } from "../../redux/shop/shop.selector";

import WithSpinner from "../../components/with-spinner/with-spinner";

import CollectionPage from "../collection/collection.page";
import CollectionsOverviewContainer from "../../components/collection-overview/collections-overview.container";

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {
  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }
  render() {
    const { match, isCollectionLoading } = this.props;
    return (
      <div className="shop">
        <Route
          exact
          path={`${match.path}`}
          component={CollectionsOverviewContainer}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={props => (
            <CollectionPageWithSpinner
              isLoading={!isCollectionLoading}
              {...props}
            />
          )}
          // component={CollectionPage}
        />
      </div>
    );
  }
}

const mapStateToProps = () =>
  createStructuredSelector({
    isCollectionLoading: selectIsCollectionLoading
  });

const mapDispatchStateToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(mapStateToProps, mapDispatchStateToProps)(ShopPage);
