import React from "react";

import { connect } from "react-redux";

import { clearItem, addItem, removeItem } from "../../redux/cart/cart.action";

import "./checkout-item.scss";
// import { selectCartItems } from '../../redux/cart/cart.selector';

const CheckoutItem = ({ cartItem, clearItem }) => {
  const { name, imageUrl, price, quantity } = cartItem;

  return (
    <div className="checkout-item">
      <div className="image-container">
        <img src={imageUrl} alt="item"></img>
      </div>
      <span className="name">{name}</span>
      <span className="quantity">
        <div className="arrow" onClick={() => addItem(cartItem)}>&#10094;</div>
        <span className='value'>{quantity}</span>
        <div className="arrow" onClick={() => removeItem(cartItem)}>&#10095;</div>
      </span>
      <span className="price">{price}</span>
      <span className="clear-button" onClick={() => clearItem(cartItem)}>
        &#10005;
      </span>
    </div>
  );
};

const mapDispatchProps = dispatch => ({
  clearItem: item => dispatch(clearItem(item)),
  addItem: item => dispatch(addItem(item)),
  removeItem: item => dispatch(removeItem(item))
});

export default connect(
  null,
  mapDispatchProps
)(CheckoutItem);
