import React from "react";

import "./form-input.scss";

const FormInput = ({ handleChange, label, ...otherProps }) => (
  <div className="group">
    <input
      className="form-input"
      onChange={this.props.handleChange}
      {...otherProps}
    />
    {label ? (
      <label
        className={`${
          this.props.value.length ? "shrink" : ""
        } form-input-label`}
      ></label>
    ) : null}
  </div>
);

export default FormInput;
